export const TETROMINOS = {
    0: { shape: [[0]], color: '0,0,0'},
    I: {
        shape: [
            [0,'I',0,0],
            [0,'I',0,0],
            [0,'I',0,0],
            [0,'I',0,0],
        ],
        color: '80, 227, 230'
    },
    J: {
        shape: [
            [0,'J',0],
            [0,'J',0],
            ['J','J',0],
        ],
        color: '36, 95, 223'
    },
    L: {
        shape: [
            [0,'J',0],
            [0,'J',0],
            [0,'J','L'],
        ],
        color: '223, 137, 36'
    },
    O: {
        shape: [
            ['O','O'],
            ['O','O']
        ],
        color: '223, 217, 36'
    },
    T: {
        shape: [
            ['T','T','T'],
            [0,'T',0],
            [0,'T',0],
        ],
        color: '132, 61, 198'
    },
    Z: {
        shape: [
            ['Z','Z',0],
            [0,'Z','Z'],
            [0,0,0],
        ],
        color: '227, 78, 78'
    },
}

export const randomTetromino = () => {
    const tetrominos = 'IJLOSTZ'
    const randtetromino = tetrominos[Math.floor(Math.random() * tetrominos.length)]
    console.log(TETROMINOS)
    return TETROMINOS[randtetromino]

}
