import { useState } from 'react';
import { randomTetromino } from '../tetrominos';

export const userPlayer = () => {
    const [player, setplayer] = useState({
        pos: { x: 0, y: 0 },
        tetromino: randomTetromino().shape,
        collided: false

    });
    return [player];
}
