import { useState } from 'react';
import { randomTetromino } from '../tetrominos';

export const usePlayer = () => {
    const [player, setplayer] = useState({
        pos: { x: 0, y: 0 },
        tetromino: randomTetromino().shape,
        collided: false

    });

    const updatePlayerPos = ({ x, y, collided }) => {
        setplayer(prev => ({
            ...prev,
            pos: { x: (prev.pos.x += x)}
        }))
    }


    return [player];
}
